            aiEndpointService.askAQuestionEvent(question)
                    .onItem()
                    .invoke(event -> {
                        switch (event) {
                            case io.quarkiverse.langchain4j.runtime.aiservice.ChatEvent.PartialResponseEvent e -> {
                                io.quarkus.logging.Log.info(e.getChunk());
                            }
                            case io.quarkiverse.langchain4j.runtime.aiservice.ChatEvent.BeforeToolExecutionEvent e -> {
                                io.quarkus.logging.Log.info(java.lang.String.format("âš ï¸ Please valid the tool usage: %s âš ï¸%n", e.getRequest().name()));
                                java.util.Scanner scanner = new java.util.Scanner(java.lang.System.in);
                                if (scanner.next()
                                        .equals("ok")) {
                                    io.quarkus.logging.Log.info(java.lang.String.format("ðŸ”§ Using tool: %s", e.getRequest().name()));
                                } else {
                                    throw new fr.wilda.picocli.sdk.ai.McpToolsException();
                                }
                            }
                            default -> {
                            }
                        }
                    })
                    .collect()
                    .asList()
